export const CodeTabs = ({ tabs, defaultTab }) => {
  const [activeTab, setActiveTab] = React.useState(defaultTab || tabs[0]?.id);
  const currentTab = tabs.find((tab) => tab.id === activeTab) || tabs[0];

return (
    <div className="flex flex-col gap-6 w-full">
      <style dangerouslySetInnerHTML={{__html: `
        .code-tab-btn {
          padding: 0;
          border: none;
          background: transparent;
          cursor: pointer;
          font-family: inherit;
          font-size: 14px;
          transition: color 0.2s ease;
          color: #71717a;
          font-weight: 400;
        }
        .code-tab-btn.active {
          color: #18181b;
          font-weight: 600;
        }
        .dark .code-tab-btn.active {
          color: #f4f4f5;
        }
        .code-tab-bracket {
          margin-right: 4px;
          visibility: hidden;
        }
        .code-tab-bracket-right {
          margin-left: 4px;
          visibility: hidden;
        }
        .code-tab-btn.active .code-tab-bracket,
        .code-tab-btn.active .code-tab-bracket-right {
          visibility: visible;
        }
        .kw { color: #ff4017; }
        .str { color: #c3e88d; }
        .num { color: #c792ea; }
        .cmt { color: #71717a; }
      `}} />
      <div className="grid grid-cols-2 gap-4 md:flex md:gap-8">
        {tabs.map((tab) => {
          const isActive = activeTab === tab.id;
          return (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={isActive ? 'code-tab-btn active' : 'code-tab-btn'}
            >
              <span className="code-tab-bracket">[</span>
              {tab.label}
              <span className="code-tab-bracket-right">]</span>
            </button>
          );
        })}
      </div>

<style dangerouslySetInnerHTML={{__html: `
        .code-box {
          position: relative;
          border-radius: 16px;
          border: 1px solid #27272a;
          background: #09090b;
          padding: 20px;
          overflow: auto;
        }
        .code-box pre {
          margin: 0;
          padding: 0;
          overflow: auto;
          font-size: 13px;
          line-height: 1.8;
          font-family: "JetBrains Mono", "Fira Code", Monaco, Consolas, monospace;
        }
        .code-box code {
          color: #e5e7eb;
          white-space: pre;
        }
        .code-lang {
          display: block;
          font-size: 10px;
          text-transform: uppercase;
          letter-spacing: 0.15em;
          color: #ff4017;
          margin-bottom: 16px;
          font-weight: 600;
        }
      `}} />
      <div className="code-box">
        <pre>
          <code>
            {currentTab?.language && (
              <span className="code-lang">{currentTab.language}</span>
            )}
            {currentTab?.code}
          </code>
        </pre>
      </div>
    </div>
  );
};

export const codeTabs = [
    {
        id: 'agents',
        label: 'Agents',
        language: 'python',
